task.solution:
.cyber-card {
  background: #0a0a0a;
  border: 2px solid #00ffff;
  box-shadow: 0 0 20px rgba(0,255,255,0.5);
  padding: 30px;
  border-radius: 10px;
  max-width: 300px;
}

.card-title {
  color: #00ffff;
  margin: 0 0 15px 0;
  font-family: monospace;
}

.card-text {
  color: #ffffff;
  margin: 0 0 20px 0;
  font-size: 14px;
  line-height: 1.6;
}

.card-btn {
  background: linear-gradient(135deg, #00ffff, #00cccc);
  color: #000;
  border: none;
  padding: 10px 25px;
  border-radius: 5px;
  font-weight: bold;
  cursor: pointer;
}

task.validate:
(html, css) => {
  const checks = [
    css.includes('#0a0a0a'),
    css.includes('#00ffff'),
    css.includes('box-shadow'),
    css.includes('border-radius'),
    css.includes('padding'),
    (css.includes('linear-gradient') || css.includes('gradient'))
  ];
  return checks.filter(Boolean).length >= 5;
}

