task.solution:
.cyber-card {
  /* ... previous styles ... */
  transition: all 0.3s ease;
}

.cyber-card:hover {
  box-shadow: 0 0 30px rgba(0,255,255,0.8);
  transform: translateY(-5px);
}

.card-btn {
  /* ... previous styles ... */
  transition: all 0.3s ease;
}

.card-btn:hover {
  background: linear-gradient(135deg, #00ffff, #00e6e6);
  transform: scale(1.05);
}

@media (max-width: 768px) {
  .cyber-card {
    padding: 20px;
    max-width: 100%;
    margin: 10px;
  }

  .card-title {
    font-size: 1.2em;
  }

  .card-text {
    font-size: 0.9em;
  }

  .card-btn {
    padding: 8px 20px;
    width: 100%;
  }
}

/* Example solution - yours can be different! */
.card-title {
  color: #00ffff;
  margin: 0 0 15px 0;
  font-family: 'Courier New', monospace;
  letter-spacing: 2px;
  text-shadow: 0 0 10px rgba(0,255,255,0.5);
  font-size: 1.5em;
}

@keyframes pulse {
  0%, 100% { box-shadow: 0 0 20px rgba(0,255,255,0.5); }
  50% { box-shadow: 0 0 30px rgba(0,255,255,0.8); }
}

.cyber-card {
  animation: pulse 3s infinite;
}
